class ConnectionPool {
  constructor(maxSize) {
    this.maxSize = maxSize;
    this.connections = [];
  }

  acquire() {
    if (this.connections.length < this.maxSize) {
      const conn = { id: Date.now() };
      this.connections.push(conn);
      return conn;
    }
    return null;
  }

  release(conn) {
    const index = this.connections.indexOf(conn);
    if (index > -1) {
      this.connections.splice(index, 1);
    }
  }
}

